INSERT INTO `node_type` VALUES ('blog','Blog entry','blog','A <em>blog entry</em> is a single post to an online journal, or <em>blog</em>.','',1,'Title',1,'Body',0,0,0,1,'blog'),('book','Book page','node','A <em>book page</em> is a page of content, organized into a collection of related entries collectively known as a <em>book</em>. A <em>book page</em> automatically displays links to adjacent pages, providing a simple navigation system for organizing and reviewing structured content.','',1,'Title',1,'Body',0,1,1,0,'book'),('event','Event','node','An <em>event</em> has a date and a location and we want people to attend it, so we describe it well.','',1,'Title',1,'Body',0,1,1,0,''),('feed','Feed','node','Items from these feeds will be turned into nodes.','',1,'Title',1,'Body',0,1,1,0,'feed'),('page','Page','node','A <em>page</em>, similar in form to a <em>story</em>, is a simple method for creating and displaying information that rarely changes, such as an \"About us\" section of a website. By default, a <em>page</em> entry does not allow visitor comments and is not featured on the site\'s initial home page.','',1,'Title',1,'Body',0,1,1,0,'page'),('simplenews','Newsletter issue','node','A newsletter issue to be sent to subscribed email addresses.','',1,'Title',1,'Body',0,1,0,0,'simplenews'),('story','Story','node','A <em>story</em>, similar in form to a <em>page</em>, is ideal for creating and displaying content that informs or engages website visitors. Press releases, site announcements, and informal blog-like entries may all be created with a <em>story</em> entry. By default, a <em>story</em> entry is automatically featured on the site\'s initial home page, and provides the ability to post comments.','',1,'Title',1,'Body',0,1,1,0,'story');
